import React, { useState } from 'react';
import LandingPage from './components/LandingPage';
import Dashboard from './components/Dashboard';

const App: React.FC = () => {
  const [view, setView] = useState<'landing' | 'dashboard'>('landing');

  return (
    <div className="min-h-screen">
      {view === 'landing' ? (
        <LandingPage onEnterDashboard={() => setView('dashboard')} />
      ) : (
        <Dashboard onBackToLanding={() => setView('landing')} />
      )}
    </div>
  );
};

export default App;

import React, { useState } from 'react';
import { 
  LayoutDashboard, 
  Upload, 
  BarChart2, 
  Wallet, 
  Wand2, 
  Settings, 
  LogOut, 
  Bell,
  Search,
  Plus
} from 'lucide-react';
import Overview from './Overview';
import AIStudio from './AIStudio';
import Analytics from './Analytics';
import WalletView from './WalletView';

interface DashboardProps {
  onBackToLanding: () => void;
}

type Tab = 'overview' | 'analytics' | 'wallet' | 'ai-studio' | 'settings';

const Dashboard: React.FC<DashboardProps> = ({ onBackToLanding }) => {
  const [activeTab, setActiveTab] = useState<Tab>('overview');

  const navItems = [
    { id: 'overview', label: 'ওভারভিউ', icon: LayoutDashboard },
    { id: 'analytics', label: 'অ্যানালিটিক্স', icon: BarChart2 },
    { id: 'wallet', label: 'রয়্যালটি ও পেমেন্ট', icon: Wallet },
    { id: 'ai-studio', label: 'AI স্টুডিও', icon: Wand2 },
    { id: 'settings', label: 'সেটিংস', icon: Settings },
  ];

  return (
    <div className="flex h-screen bg-slate-950 text-slate-100 overflow-hidden">
      {/* Sidebar */}
      <aside className="w-64 border-r border-slate-800 bg-slate-900/50 hidden md:flex flex-col">
        <div className="p-6 flex items-center gap-3">
          <div className="w-8 h-8 bg-indigo-600 rounded-lg flex items-center justify-center">
            <Upload className="text-white" size={18} />
          </div>
          <span className="text-xl font-bold">সুরবিতান</span>
        </div>

        <nav className="flex-1 px-4 py-4 space-y-2">
          {navItems.map((item) => (
            <button
              key={item.id}
              onClick={() => setActiveTab(item.id as Tab)}
              className={`w-full flex items-center gap-3 px-4 py-3 rounded-xl transition-all ${
                activeTab === item.id 
                  ? 'bg-indigo-600/10 text-indigo-400 border border-indigo-500/20' 
                  : 'text-slate-400 hover:bg-slate-800 hover:text-slate-100'
              }`}
            >
              <item.icon size={20} />
              <span className="font-medium">{item.label}</span>
            </button>
          ))}
        </nav>

        <div className="p-4 border-t border-slate-800">
          <button 
            onClick={onBackToLanding}
            className="w-full flex items-center gap-3 px-4 py-3 text-slate-400 hover:text-red-400 transition-all"
          >
            <LogOut size={20} />
            <span>লগ আউট</span>
          </button>
        </div>
      </aside>

      {/* Main Content */}
      <main className="flex-1 flex flex-col overflow-hidden">
        {/* Header */}
        <header className="h-16 border-b border-slate-800 flex items-center justify-between px-6 bg-slate-950/50">
          <div className="relative w-96 hidden lg:block">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-500" size={18} />
            <input 
              type="text" 
              placeholder="আপনার গান বা অ্যালবাম খুঁজুন..."
              className="w-full bg-slate-900 border border-slate-800 rounded-full py-2 pl-10 pr-4 focus:outline-none focus:border-indigo-500 transition-all text-sm"
            />
          </div>

          <div className="flex items-center gap-4">
            <button className="relative p-2 text-slate-400 hover:text-slate-100 transition-all">
              <Bell size={20} />
              <span className="absolute top-2 right-2 w-2 h-2 bg-indigo-500 rounded-full"></span>
            </button>
            <div className="flex items-center gap-3 pl-4 border-l border-slate-800">
              <div className="text-right hidden sm:block">
                <p className="text-sm font-semibold">আর্টিস্ট নেম</p>
                <p className="text-xs text-slate-500">ফ্রি প্ল্যান</p>
              </div>
              <img 
                src="https://picsum.photos/seed/artist/40/40" 
                alt="Profile" 
                className="w-10 h-10 rounded-full border border-slate-700"
              />
            </div>
          </div>
        </header>

        {/* Dynamic Content */}
        <div className="flex-1 overflow-y-auto p-6">
          <div className="max-w-6xl mx-auto">
            {activeTab === 'overview' && <Overview />}
            {activeTab === 'analytics' && <Analytics />}
            {activeTab === 'wallet' && <WalletView />}
            {activeTab === 'ai-studio' && <AIStudio />}
            {activeTab === 'settings' && (
              <div className="text-center py-20 text-slate-500 italic">সেটিংস ফিচারটি শীঘ্রই আসছে...</div>
            )}
          </div>
        </div>
      </main>

      {/* Mobile Nav */}
      <div className="md:hidden fixed bottom-0 left-0 w-full bg-slate-900 border-t border-slate-800 flex justify-around p-3 z-50">
        {navItems.slice(0, 4).map((item) => (
          <button
            key={item.id}
            onClick={() => setActiveTab(item.id as Tab)}
            className={`p-2 rounded-lg ${activeTab === item.id ? 'text-indigo-400' : 'text-slate-500'}`}
          >
            <item.icon size={24} />
          </button>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;

import React, { useState } from 'react';
import { Wand2, Mic2, Sparkles, Send, Loader2, Music4 } from 'lucide-react';
import { GoogleGenAI } from '@google/genai';

const AIStudio: React.FC = () => {
  const [trackDescription, setTrackDescription] = useState('');
  const [masteringFeedback, setMasteringFeedback] = useState('');
  const [marketingCopy, setMarketingCopy] = useState('');
  const [loading, setLoading] = useState(false);

  const handleGenerate = async () => {
    if (!trackDescription.trim()) return;
    setLoading(true);

    try {
      const ai = new GoogleGenAI({ apiKey: process.env.API_KEY || '' });
      const response = await ai.models.generateContent({
        model: 'gemini-3-flash-preview',
        contents: `I am a music distribution service AI. Based on this track description: "${trackDescription}", provide:
        1. A brief professional mastering advice (technical details like EQ, compression, reverb).
        2. Three punchy social media marketing lines in Bengali to hype the release.
        Format the output clearly.`,
        config: {
          systemInstruction: "You are a world-class music producer and marketing expert. Always provide encouraging and professional advice."
        }
      });

      const text = response.text;
      setMasteringFeedback(text || 'দুঃখিত, কোনো ফলাফল পাওয়া যায়নি।');
    } catch (error) {
      console.error(error);
      setMasteringFeedback('Error processing request.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="space-y-8 animate-in fade-in slide-in-from-bottom-4 duration-500">
      <div className="bg-gradient-to-br from-indigo-900/40 to-purple-900/40 border border-indigo-500/20 p-8 rounded-3xl relative overflow-hidden">
        <div className="relative z-10">
          <div className="flex items-center gap-3 mb-4">
            <div className="p-3 bg-indigo-500 rounded-2xl shadow-lg shadow-indigo-500/20">
              <Sparkles className="text-white" size={24} />
            </div>
            <h2 className="text-3xl font-bold">AI স্টুডিও</h2>
          </div>
          <p className="text-slate-300 max-w-xl">
            আপনার গানের বর্ণনা দিন, আমাদের AI আপনাকে প্রফেশনাল মাস্টারিং পরামর্শ এবং সোশ্যাল মিডিয়া কপি তৈরি করে দেবে।
          </p>
        </div>
        <div className="absolute right-[-5%] top-[-10%] opacity-10 pointer-events-none">
          <Music4 size={300} className="text-white" />
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        <div className="bg-slate-900 border border-slate-800 p-6 rounded-2xl h-fit">
          <h3 className="text-xl font-bold mb-4 flex items-center gap-2">
            <Mic2 className="text-indigo-400" size={20} />
            আপনার গান সম্পর্কে বলুন
          </h3>
          <textarea
            value={trackDescription}
            onChange={(e) => setTrackDescription(e.target.value)}
            placeholder="যেমন: এটি একটি স্লো মেলোডিয়াস বাংলা পপ গান, যেখানে ফোক ফ্লেভার আছে..."
            className="w-full h-40 bg-slate-950 border border-slate-800 rounded-xl p-4 focus:outline-none focus:border-indigo-500 transition-all resize-none mb-4"
          />
          <button
            onClick={handleGenerate}
            disabled={loading || !trackDescription}
            className="w-full bg-indigo-600 hover:bg-indigo-700 disabled:opacity-50 disabled:cursor-not-allowed py-3 rounded-xl font-bold flex items-center justify-center gap-2 transition-all shadow-lg shadow-indigo-500/20"
          >
            {loading ? <Loader2 className="animate-spin" /> : <Send size={18} />}
            অ্যানালাইজ করুন
          </button>
        </div>

        <div className="bg-slate-900 border border-slate-800 p-6 rounded-2xl min-h-[400px]">
          <h3 className="text-xl font-bold mb-4 flex items-center gap-2">
            <Wand2 className="text-purple-400" size={20} />
            AI ফলাফল
          </h3>
          {masteringFeedback ? (
            <div className="prose prose-invert max-w-none">
              <div className="bg-slate-950/50 p-5 rounded-xl border border-slate-800 text-slate-300 whitespace-pre-wrap leading-relaxed">
                {masteringFeedback}
              </div>
            </div>
          ) : (
            <div className="h-full flex flex-col items-center justify-center text-slate-500 py-10">
              <Sparkles size={48} className="mb-4 opacity-20" />
              <p className="text-center italic">বাম দিকে বর্ণনা দিয়ে জেনারেট বাটনে ক্লিক করুন।</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AIStudio;

