import React, { useState } from 'react';
import { Wand2, Mic2, Sparkles, Send, Loader2, Music4, Headphones, FileAudio } from 'lucide-react';
import { GoogleGenAI } from "@google/genai";

const AIStudio: React.FC = () => {
  const [trackDescription, setTrackDescription] = useState('');
  const [activeMode, setActiveMode] = useState<'mastering' | 'marketing'>('mastering');
  const [aiResponse, setAiResponse] = useState('');
  const [loading, setLoading] = useState(false);

  const handleGenerate = async () => {
    if (!trackDescription.trim()) return;
    setLoading(true);

    try {
      const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

      const prompt = activeMode === 'mastering' 
        ? `You are an expert Audio Mastering Engineer. Based on this description of a music mix: "${trackDescription}", provide a professional mastering analysis. 
           Include specific advice on:
           1. Dynamic range and compression needs.
           2. EQ balance (Highs, Mids, Lows).
           3. Stereo width and imaging.
           4. Final loudness (LUFS) targets.
           Give the advice in a supportive tone for an independent artist. Language: English but include a brief summary in Bengali at the end.`
        : `You are a music marketing genius. For a track described as: "${trackDescription}", generate a comprehensive release plan.
           1. 3 catchy Instagram/Facebook captions in Bengali.
           2. 5 relevant hashtags.
           3. A strategy for playlist pitching.
           4. A 'hook' idea for a TikTok/Reels video.
           Language: Bengali.`;

      const response = await ai.models.generateContent({
        model: 'gemini-3-flash-preview',
        contents: prompt,
        config: {
          systemInstruction: activeMode === 'mastering' 
            ? "You are a world-class mastering engineer like Bob Ludwig. Be technical but accessible."
            : "You are a viral music marketer. Know how to build hype for independent artists."
        }
      });

      setAiResponse(response.text || 'দুঃখিত, কোনো ফলাফল পাওয়া যায়নি।');
    } catch (error) {
      console.error(error);
      setAiResponse('Error processing request.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="space-y-8 animate-in fade-in slide-in-from-bottom-4 duration-500">
      <div className="bg-gradient-to-br from-indigo-900/40 to-purple-900/40 border border-indigo-500/20 p-8 rounded-3xl relative overflow-hidden">
        <div className="relative z-10">
          <div className="flex items-center gap-3 mb-4">
            <div className="p-3 bg-indigo-500 rounded-2xl shadow-lg shadow-indigo-500/20">
              <Sparkles className="text-white" size={24} />
            </div>
            <h2 className="text-3xl font-bold">সুরবিতান AI স্টুডিও</h2>
          </div>
          <p className="text-slate-300 max-w-xl">
            আপনার মিউজিককে প্রফেশনাল রূপ দিতে AI এর সাহায্য নিন। মিক্স মাস্টারিং পরামর্শ থেকে মার্কেটিং প্ল্যান—সবই এখন এক জায়গায়।
          </p>
        </div>
        <div className="absolute right-[-5%] top-[-10%] opacity-10 pointer-events-none">
          <Music4 size={300} className="text-white" />
        </div>
      </div>

      {/* Mode Switcher */}
      <div className="flex gap-4 p-1 bg-slate-900 border border-slate-800 rounded-2xl w-fit">
        <button 
          onClick={() => { setActiveMode('mastering'); setAiResponse(''); }}
          className={`px-6 py-2.5 rounded-xl font-bold flex items-center gap-2 transition-all ${
            activeMode === 'mastering' ? 'bg-indigo-600 text-white shadow-lg' : 'text-slate-500 hover:text-slate-300'
          }`}
        >
          <Headphones size={18} />
          AI মাস্টারিং অ্যাডভাইস
        </button>
        <button 
          onClick={() => { setActiveMode('marketing'); setAiResponse(''); }}
          className={`px-6 py-2.5 rounded-xl font-bold flex items-center gap-2 transition-all ${
            activeMode === 'marketing' ? 'bg-indigo-600 text-white shadow-lg' : 'text-slate-500 hover:text-slate-300'
          }`}
        >
          <Wand2 size={18} />
          মার্কেটিং কপি জেনারেটর
        </button>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        <div className="bg-slate-900 border border-slate-800 p-6 rounded-2xl h-fit">
          <h3 className="text-xl font-bold mb-4 flex items-center gap-2">
            <Mic2 className="text-indigo-400" size={20} />
            {activeMode === 'mastering' ? 'মিক্স সম্পর্কে বিস্তারিত বলুন' : 'গান সম্পর্কে বিস্তারিত বলুন'}
          </h3>
          <textarea
            value={trackDescription}
            onChange={(e) => setTrackDescription(e.target.value)}
            placeholder={activeMode === 'mastering' 
              ? "যেমন: আমার মিক্সটিতে বেজ ড্রাম অনেক ভারি মনে হচ্ছে, কিন্তু ভোকালটা পরিষ্কার শোনা যাচ্ছে না..."
              : "যেমন: একটি বিরহের গান, যেখানে একাকীত্বের অনুভূতি ফুটিয়ে তোলা হয়েছে..."
            }
            className="w-full h-48 bg-slate-950 border border-slate-800 rounded-xl p-4 focus:outline-none focus:border-indigo-500 transition-all resize-none mb-4"
          />
          <button
            onClick={handleGenerate}
            disabled={loading || !trackDescription}
            className="w-full bg-indigo-600 hover:bg-indigo-700 disabled:opacity-50 disabled:cursor-not-allowed py-4 rounded-xl font-bold flex items-center justify-center gap-2 transition-all shadow-lg shadow-indigo-500/20"
          >
            {loading ? <Loader2 className="animate-spin" /> : <Send size={18} />}
            {activeMode === 'mastering' ? 'অ্যানালাইজ করুন' : 'প্ল্যান জেনারেট করুন'}
          </button>
        </div>

        <div className="bg-slate-900 border border-slate-800 p-6 rounded-2xl min-h-[400px] flex flex-col">
          <h3 className="text-xl font-bold mb-4 flex items-center gap-2">
            <Sparkles className="text-purple-400" size={20} />
            AI ফলাফল
          </h3>
          {aiResponse ? (
            <div className="flex-1 overflow-y-auto">
              <div className="bg-slate-950/50 p-6 rounded-xl border border-slate-800 text-slate-300 whitespace-pre-wrap leading-relaxed text-sm md:text-base">
                {aiResponse}
              </div>
              <div className="mt-4 flex gap-2">
                <button className="text-xs bg-slate-800 hover:bg-slate-700 px-3 py-1.5 rounded-lg border border-slate-700">কপি করুন</button>
                <button className="text-xs bg-slate-800 hover:bg-slate-700 px-3 py-1.5 rounded-lg border border-slate-700">সেভ করুন</button>
              </div>
            </div>
          ) : (
            <div className="flex-1 flex flex-col items-center justify-center text-slate-500 py-10">
              <div className="w-16 h-16 bg-slate-800 rounded-full flex items-center justify-center mb-4 animate-pulse">
                {activeMode === 'mastering' ? <Headphones size={32} className="opacity-30" /> : <FileAudio size={32} className="opacity-30" />}
              </div>
              <p className="text-center italic max-w-[250px]">বাম দিকে তথ্য প্রদান করে এআই এর ম্যাজিক দেখুন।</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AIStudio;

