        {step === 3 && (
          <div className="space-y-8">
            <div className="flex items-center gap-4 p-6 bg-indigo-500/5 border border-indigo-500/20 rounded-2xl">
              <Sparkles className="text-indigo-400 shrink-0" size={24} />
              <div>
