import React, { useState } from 'react';
import { 
  Video, 
  FileVideo, 
  ImageIcon, 
  Tag, 
  ChevronRight, 
  Upload, 
  CheckCircle, 
  Loader2,
  Sparkles,
  Info,
  ShieldCheck,
  Globe
} from 'lucide-react';
import { GoogleGenAI } from "@google/genai";
import { VideoMetadata } from '../types';

interface VideoUploaderProps {
  onComplete?: () => void;
}

const VideoUploader: React.FC<VideoUploaderProps> = ({ onComplete }) => {
  const [step, setStep] = useState(0); 
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [validationResult, setValidationResult] = useState<string | null>(null);
  const [metadata, setMetadata] = useState<VideoMetadata>({
    title: '',
    artist: '',
    director: '',
    producer: '',
    productionCompany: '',
    label: '',
    isrc: '',
    upc: '',
    genre: 'Pop',
    releaseDate: '',
    explicit: false,
    description: ''
  });

  const genres = ['Pop', 'Rock', 'Electronic', 'Hip Hop', 'Latin', 'R&B', 'Country', 'Folk'];

  const handleAudit = async () => {
    setIsSubmitting(true);
    try {
      const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });
      const prompt = `Act as a Vevo and Apple Music Video Compliance Auditor. 
      Check this music video submission for metadata standards:
      - Title: ${metadata.title} (Vevo standard: must not include 'Official Video' or '4K')
      - Artist: ${metadata.artist}
      - Director: ${metadata.director}
      - Production Company: ${metadata.productionCompany}
      - Description: ${metadata.description}

      Provide a strict audit report with 3 concise points regarding compliance with Vevo and Apple Music metadata rules. 
      If there are issues with the title formatting (e.g. including brackets like [Official Music Video]), flag them.`;

      const response = await ai.models.generateContent({
        model: 'gemini-3-flash-preview',
        contents: prompt,
      });
      setValidationResult(response.text);
      setStep(2);
    } catch (error) {
      console.error(error);
      setStep(2); 
    } finally {
      setIsSubmitting(false);
    }
  };

  const finalSubmit = async () => {
    setIsSubmitting(true);
    await new Promise(r => setTimeout(r, 2500)); // Simulate delivery to Vevo ingestion servers
    setIsSubmitting(false);
    if (onComplete) onComplete();
  };

  return (
    <div className="max-w-4xl mx-auto animate-in fade-in slide-in-from-bottom-4 duration-500 pb-20">
      <div className="mb-10 text-center">
        <h2 className="text-3xl font-bold mb-2 flex items-center justify-center gap-3">
          <Video className="text-rose-500" size={32} />
          Premium Video Distribution
        </h2>
        <p className="text-slate-400">Deliver your music videos to Vevo, Apple Music, and Tidal with OrbitX.</p>
      </div>

      {/* Stepper */}
      <div className="flex items-center justify-between mb-12 relative max-w-md mx-auto">
        <div className="absolute top-1/2 left-0 w-full h-0.5 bg-slate-800 -z-10 -translate-y-1/2"></div>
        {[0, 1, 2].map((s) => (
          <div 
            key={s}
            className={`w-10 h-10 rounded-full flex items-center justify-center font-bold border-2 transition-all ${
              step >= s ? 'bg-rose-600 border-rose-600 text-white' : 'bg-slate-900 border-slate-800 text-slate-500'
            }`}
          >
            {step > s ? <CheckCircle size={20} /> : s + 1}
          </div>
        ))}
      </div>

      <div className="bg-slate-900 border border-slate-800 rounded-3xl p-8 shadow-xl">
        {step === 0 && (
          <div className="space-y-8">
            <h3 className="text-xl font-bold flex items-center gap-2">
              <FileVideo className="text-rose-400" size={20} />
              Step 1: Video Asset Upload
            </h3>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              <div className="space-y-4">
                <label className="block text-sm font-medium text-slate-400">
                  Master Video File (ProRes 422 or High-bitrate MP4)
                </label>
                <div className="border-2 border-dashed border-slate-800 rounded-2xl p-8 flex flex-col items-center justify-center gap-4 transition-all bg-slate-950/50 cursor-pointer group hover:border-rose-500/50">
                  <div className="p-4 bg-rose-500/10 text-rose-400 rounded-full group-hover:scale-110 transition-transform">
                    <Upload size={32} />
                  </div>
                  <div className="text-center">
                    <p className="font-bold">Select 1080p/4K Master</p>
                    <p className="text-xs text-slate-500 mt-1">Maximum file size: 10GB</p>
                  </div>
                </div>
              </div>

              <div className="space-y-4">
                <label className="block text-sm font-medium text-slate-400">
                  High-Res Thumbnail (1920x1080px, no text)
                </label>
                <div className="border-2 border-dashed border-slate-800 rounded-2xl p-8 flex flex-col items-center justify-center gap-4 hover:border-indigo-500/50 transition-all bg-slate-950/50 cursor-pointer group">
                  <div className="p-4 bg-indigo-500/10 rounded-full text-indigo-400 group-hover:scale-110 transition-transform">
                    <ImageIcon size={32} />
                  </div>
                  <div className="text-center">
                    <p className="font-bold">Upload Custom Poster</p>
                    <p className="text-xs text-slate-500 mt-1">Vevo requires text-free images</p>
                  </div>
                </div>
              </div>
            </div>

            <div className="bg-slate-950/50 border border-slate-800 p-4 rounded-xl flex items-start gap-3">
              <Info className="text-indigo-400 shrink-0 mt-1" size={18} />
              <div className="text-xs text-slate-500 leading-relaxed">
                <span className="font-bold text-slate-300">Tip:</span> Vevo and Apple Music are very strict about video quality. Ensure your video has no black bars (letterboxing) and the frame rate matches the source master.
              </div>
            </div>

            <div className="flex justify-end">
              <button 
                onClick={() => setStep(1)} 
                className="bg-rose-600 hover:bg-rose-700 text-white px-8 py-3 rounded-xl font-bold flex items-center gap-2 transition-all"
              >
                Continue to Metadata <ChevronRight size={18} />
              </button>
            </div>
          </div>
        )}

        {step === 1 && (
          <div className="space-y-6">
            <h3 className="text-xl font-bold flex items-center gap-2">
              <Tag className="text-rose-400" size={20} />
              Step 2: Vevo & Apple Music Metadata
            </h3>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div className="space-y-2">
                <label className="text-sm text-slate-400">Video Title</label>
                <input 
                  type="text" 
                  className="w-full bg-slate-950 border border-slate-800 rounded-xl px-4 py-3 focus:border-rose-500 outline-none"
                  placeholder="Official Track Title"
                  value={metadata.title}
                  onChange={e => setMetadata({...metadata, title: e.target.value})}
                />
              </div>
              <div className="space-y-2">
                <label className="text-sm text-slate-400">Primary Artist</label>
                <input 
                  type="text" 
                  className="w-full bg-slate-950 border border-slate-800 rounded-xl px-4 py-3 focus:border-rose-500 outline-none"
                  placeholder="Artist Name"
                  value={metadata.artist}
                  onChange={e => setMetadata({...metadata, artist: e.target.value})}
                />
              </div>
              <div className="space-y-2">
                <label className="text-sm text-slate-400">Director</label>
                <input 
                  type="text" 
                  className="w-full bg-slate-950 border border-slate-800 rounded-xl px-4 py-3 focus:border-rose-500 outline-none"
                  placeholder="Director Name"
                  value={metadata.director}
                  onChange={e => setMetadata({...metadata, director: e.target.value})}
                />
              </div>
              <div className="space-y-2">
                <label className="text-sm text-slate-400">Production Company</label>
                <input 
                  type="text" 
                  className="w-full bg-slate-950 border border-slate-800 rounded-xl px-4 py-3 focus:border-rose-500 outline-none"
                  placeholder="Studio Name"
                  value={metadata.productionCompany}
                  onChange={e => setMetadata({...metadata, productionCompany: e.target.value})}
                />
              </div>
              <div className="space-y-2">
                <label className="text-sm text-slate-400">Label / Rights Holder</label>
                <input 
                  type="text" 
                  className="w-full bg-slate-950 border border-slate-800 rounded-xl px-4 py-3 focus:border-rose-500 outline-none"
                  placeholder="Record Label"
                  value={metadata.label}
                  onChange={e => setMetadata({...metadata, label: e.target.value})}
                />
              </div>
              <div className="space-y-2">
                <label className="text-sm text-slate-400">ISRC (Optional)</label>
                <input 
                  type="text" 
                  className="w-full bg-slate-950 border border-slate-800 rounded-xl px-4 py-3 focus:border-rose-500 outline-none"
                  placeholder="QM-AAA-24-XXXXX"
                  value={metadata.isrc}
                  onChange={e => setMetadata({...metadata, isrc: e.target.value})}
                />
              </div>
              <div className="space-y-2">
                <label className="text-sm text-slate-400">Primary Genre</label>
                <select 
                  className="w-full bg-slate-950 border border-slate-800 rounded-xl px-4 py-3 focus:border-rose-500 outline-none"
                  value={metadata.genre}
                  onChange={e => setMetadata({...metadata, genre: e.target.value})}
                >
                  {genres.map(g => <option key={g} value={g}>{g}</option>)}
                </select>
              </div>
              <div className="space-y-2">
                <label className="text-sm text-slate-400">Release Date</label>
                <input 
                  type="date" 
                  className="w-full bg-slate-950 border border-slate-800 rounded-xl px-4 py-3 focus:border-rose-500 outline-none"
                  value={metadata.releaseDate}
                  onChange={e => setMetadata({...metadata, releaseDate: e.target.value})}
                />
              </div>
            </div>

            <div className="flex justify-between pt-6">
              <button onClick={() => setStep(0)} className="text-slate-400 hover:text-white px-6 py-3 font-bold">Back</button>
              <button 
                onClick={handleAudit}
                disabled={isSubmitting || !metadata.title || !metadata.artist}
                className="bg-rose-600 hover:bg-rose-700 text-white px-8 py-3 rounded-xl font-bold flex items-center gap-2 transition-all shadow-lg shadow-rose-500/20"
              >
                {isSubmitting ? <Loader2 className="animate-spin" size={18} /> : <Sparkles size={18} />}
                {isSubmitting ? 'Auditing Metadata...' : 'Run Vevo Audit'}
              </button>
            </div>
          </div>
        )}

        {step === 2 && (
          <div className="space-y-8">
            <div className="bg-indigo-500/10 border border-indigo-500/20 p-6 rounded-2xl flex items-start gap-4">
              <Sparkles className="text-indigo-400 shrink-0" size={24} />
              <div>
                <h4 className="font-bold text-indigo-400 mb-1">Gemini AI Video Audit</h4>
                <div className="text-sm text-slate-400 whitespace-pre-wrap leading-relaxed">
                  {validationResult || 'Audit complete. No formatting violations found in metadata.'}
