import React, { useState, useEffect } from 'react';
import { 
  CheckCircle2, 
  Clock, 
  Globe, 
  Video, 
  Music, 
  ExternalLink, 
  MoreVertical,
  AlertCircle,
  Link2,
  FileSearch,
  Activity,
  Filter,
  Library,
  Database
} from 'lucide-react';

interface DistributionItem {
  id: string;
  title: string;
  artist: string;
  type: 'Audio' | 'Video';
  source: 'OrbitX' | 'Distrokid' | 'Amuse';
  status: 'Distributing' | 'Live' | 'Metadata Check' | 'Quality Control' | 'Ingesting' | 'Synced';
  progress: number;
  platforms: {
    name: string;
    status: 'pending' | 'processing' | 'live' | 'error' | 'qc_check' | 'ingesting' | 'synced';
    link?: string;
    external?: boolean;
  }[];
}

const DistributionManager: React.FC = () => {
  const [filter, setFilter] = useState<'All' | 'OrbitX' | 'External'>('All');
  const [catalogItems, setCatalogItems] = useState<DistributionItem[]>([
    {
      id: '1',
      title: 'Neon Nights',
      artist: 'Artist Name',
      type: 'Video',
      source: 'OrbitX',
      status: 'Ingesting',
      progress: 72,
      platforms: [
        { name: 'Vevo API', status: 'ingesting' },
        { name: 'Apple Music', status: 'processing' },
        { name: 'Tidal', status: 'live', link: '#' },
        { name: 'YouTube OAC', status: 'live', link: '#' }
      ]
    },
    {
      id: '2',
      title: 'Electric Dreams',
      artist: 'Artist Name',
      type: 'Audio',
      source: 'OrbitX',
      status: 'Live',
      progress: 100,
      platforms: [
        { name: 'Spotify', status: 'live', link: '#' },
        { name: 'Distrokid Sync', status: 'live', link: '#', external: true },
        { name: 'AmuseLink', status: 'live', link: '#', external: true },
        { name: 'Deezer', status: 'live', link: '#' }
      ]
    }
  ]);

  // Simulate pulling external catalog if providers are connected
  useEffect(() => {
    const connected = JSON.parse(localStorage.getItem('orbitx_connected_providers') || '[]');
    if (connected.length > 0) {
      const externalItems: DistributionItem[] = [];
      if (connected.includes('Distrokid')) {
        externalItems.push({
          id: 'dk-1',
          title: 'Cyberpunk Soul',
          artist: 'Artist Name',
          type: 'Audio',
          source: 'Distrokid',
          status: 'Synced',
          progress: 100,
          platforms: [
            { name: 'Spotify', status: 'synced' },
            { name: 'Apple Music', status: 'synced' },
            { name: 'Tidal', status: 'synced' }
          ]
        });
      }
      if (connected.includes('Amuse')) {
        externalItems.push({
          id: 'am-1',
          title: 'Retro Wave',
          artist: 'Artist Name',
          type: 'Audio',
          source: 'Amuse',
          status: 'Synced',
          progress: 100,
          platforms: [
            { name: 'Spotify', status: 'synced' },
            { name: 'YouTube', status: 'synced' }
          ]
        });
      }
      setCatalogItems(prev => {
        const native = prev.filter(i => i.source === 'OrbitX');
        return [...native, ...externalItems];
      });
    }
  }, []);

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'live': 
      case 'synced': return <CheckCircle2 size={14} className="text-emerald-500" />;
      case 'processing': return <Clock size={14} className="text-amber-500 animate-pulse" />;
      case 'ingesting': return <Activity size={14} className="text-rose-500 animate-pulse" />;
      case 'qc_check': return <FileSearch size={14} className="text-blue-500" />;
      case 'error': return <AlertCircle size={14} className="text-rose-500" />;
      default: return <Clock size={14} className="text-slate-500" />;
    }
  };

  const filteredItems = catalogItems.filter(item => {
    if (filter === 'All') return true;
    if (filter === 'OrbitX') return item.source === 'OrbitX';
    return item.source !== 'OrbitX';
  });

  return (
    <div className="space-y-8 animate-in fade-in slide-in-from-bottom-4 duration-500">
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4">
        <div>
          <h2 className="text-3xl font-bold">My Music Catalog</h2>
          <p className="text-slate-400">Manage your entire discography from OrbitX and connected partners.</p>
        </div>

        <div className="flex bg-slate-900 border border-slate-800 p-1 rounded-xl">
          <button 
            onClick={() => setFilter('All')}
            className={`px-4 py-1.5 rounded-lg text-xs font-bold transition-all ${filter === 'All' ? 'bg-indigo-600 text-white shadow-lg' : 'text-slate-500 hover:text-slate-300'}`}
          >
            All
          </button>
          <button 
            onClick={() => setFilter('OrbitX')}
            className={`px-4 py-1.5 rounded-lg text-xs font-bold transition-all ${filter === 'OrbitX' ? 'bg-indigo-600 text-white shadow-lg' : 'text-slate-500 hover:text-slate-300'}`}
          >
            OrbitX
          </button>
          <button 
            onClick={() => setFilter('External')}
            className={`px-4 py-1.5 rounded-lg text-xs font-bold transition-all ${filter === 'External' ? 'bg-indigo-600 text-white shadow-lg' : 'text-slate-500 hover:text-slate-300'}`}
          >
            Synced Partners
          </button>
        </div>
      </div>

      <div className="grid grid-cols-1 gap-6">
        {filteredItems.map((item) => (
          <div key={item.id} className="bg-slate-900 border border-slate-800 rounded-3xl overflow-hidden hover:border-slate-700 transition-all group">
            <div className="p-6 md:p-8 flex flex-col md:flex-row gap-6">
              {/* Release Info */}
              <div className="flex gap-6 md:w-1/3 shrink-0">
                <div className="w-24 h-24 bg-slate-800 rounded-2xl overflow-hidden relative shrink-0">
                  <img 
                    src={`https://picsum.photos/seed/${item.id}/200/200`} 
                    className="w-full h-full object-cover" 
                    alt={item.title} 
                  />
                  <div className="absolute top-1 right-1">
                    {item.type === 'Video' ? (
                      <div className="bg-rose-600 text-[8px] font-bold px-1.5 py-0.5 rounded-md text-white flex items-center gap-1 shadow-lg">
                        <Video size={8} /> VIDEO
                      </div>
                    ) : (
                      <div className="bg-indigo-600 text-[8px] font-bold px-1.5 py-0.5 rounded-md text-white flex items-center gap-1 shadow-lg">
                        <Music size={8} /> AUDIO
                      </div>
                    )}
                  </div>
                </div>
                <div className="flex flex-col justify-center min-w-0">
                  <div className="flex items-center gap-2 mb-1">
                    <h3 className="text-xl font-bold truncate">{item.title}</h3>
                    {item.source !== 'OrbitX' && (
                      <span className="bg-slate-800 text-slate-400 text-[9px] font-black px-1.5 py-0.5 rounded uppercase border border-slate-700">
                        {item.source}
                      </span>
                    )}
                  </div>
                  <p className="text-slate-500">{item.artist}</p>
                  <div className="mt-2 flex items-center gap-2">
                    <span className={`text-[10px] font-bold px-2 py-0.5 rounded-full uppercase tracking-wider ${
                      item.status === 'Live' || item.status === 'Synced' ? 'bg-emerald-500/10 text-emerald-500' : 
                      item.status === 'Quality Control' ? 'bg-blue-500/10 text-blue-500' : 
                      item.status === 'Ingesting' ? 'bg-rose-500/10 text-rose-500' : 'bg-indigo-500/10 text-indigo-400'
                    }`}>
