import React, { useState } from 'react';
import { Wand2, Mic2, Sparkles, Send, Loader2, Music4, Headphones, FileAudio, Video, Share2, Camera } from 'lucide-react';
import { GoogleGenAI } from "@google/genai";

const AIStudio: React.FC = () => {
  const [trackDescription, setTrackDescription] = useState('');
  const [activeMode, setActiveMode] = useState<'mastering' | 'marketing' | 'video' | 'social'>('mastering');
  const [aiResponse, setAiResponse] = useState('');
  const [loading, setLoading] = useState(false);

  const handleGenerate = async () => {
    if (!trackDescription.trim()) return;
    setLoading(true);

    try {
      const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

      let prompt = '';
      let systemInstruction = '';

      if (activeMode === 'mastering') {
        systemInstruction = "You are a world-class mastering engineer like Bob Ludwig. Be technical but accessible.";
        prompt = `You are an expert Audio Mastering Engineer. Based on this description of a music mix: "${trackDescription}", provide a professional mastering analysis. 
           Include specific advice on:
           1. Dynamic range and compression needs.
           2. EQ balance (Highs, Mids, Lows).
           3. Stereo width and imaging.
           4. Final loudness (LUFS) targets.`;
      } else if (activeMode === 'marketing') {
        systemInstruction = "You are a viral music marketer. Know how to build hype for independent artists.";
        prompt = `You are a music marketing genius. For a track described as: "${trackDescription}", generate a comprehensive release plan.
           1. 3 catchy Instagram/Facebook captions.
           2. 5 relevant hashtags.
           3. A strategy for playlist pitching.
           4. A 'hook' idea for a TikTok/Reels video.`;
      } else if (activeMode === 'video') {
        systemInstruction = "You are a Vevo and YouTube SEO expert. You know exactly how to format metadata for maximum reach and platform compliance.";
        prompt = `You are a Music Video Distribution Expert. For a video described as: "${trackDescription}", provide:
           1. Vevo-Compliant Description: (Must be professional, include credits for Director, Producer, and Label, and follow Vevo's strict formatting).
           2. YouTube SEO Suite: Generate an optimized title, 500 characters of tags, and a keyword-rich description.
           3. Visual Concept: 3 detailed Thumbnail concepts that will drive high CTR.
           4. Call to Action: Craft a compelling CTA for the end of the video description.`;
      } else {
        systemInstruction = "You are a social media growth hacker for the music industry. You specialize in viral short-form content.";
        prompt = `You are a Social Media Content Strategist. For a music/video project described as: "${trackDescription}", generate a 7-day social media 'Hype Campaign':
           1. Day 1-7 Content Calendar: Specific post ideas for TikTok, Reels, and X (Twitter).
           2. Viral Hooks: 5 different 'hook' lines to use in the first 3 seconds of a video.
           3. Caption Suite: Generate 5 high-engagement captions with emojis.
           4. Engagement Strategy: One creative way to involve the audience (poll, challenge, or Q&A).`;
      }

      const response = await ai.models.generateContent({
        model: 'gemini-3-flash-preview',
        contents: prompt,
        config: {
          systemInstruction: systemInstruction
        }
      });

      setAiResponse(response.text || 'Sorry, no results found.');
    } catch (error) {
      console.error(error);
      setAiResponse('Error processing request.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="space-y-8 animate-in fade-in slide-in-from-bottom-4 duration-500">
      <div className="bg-gradient-to-br from-indigo-900/40 to-purple-900/40 border border-indigo-500/20 p-8 rounded-3xl relative overflow-hidden">
        <div className="relative z-10">
          <div className="flex items-center gap-3 mb-4">
            <div className="p-3 bg-indigo-500 rounded-2xl shadow-lg shadow-indigo-500/20">
              <Sparkles className="text-white" size={24} />
            </div>
            <h2 className="text-3xl font-bold">OrbitX AI Studio</h2>
          </div>
          <p className="text-slate-300 max-w-xl">
            Empower your music and videos with professional AI tools. Our Gemini-powered suite handles the heavy lifting of distribution and marketing.
          </p>
        </div>
        <div className="absolute right-[-5%] top-[-10%] opacity-10 pointer-events-none">
          <Music4 size={300} className="text-white" />
        </div>
      </div>

      {/* Mode Switcher */}
      <div className="flex flex-wrap gap-4 p-1.5 bg-slate-900 border border-slate-800 rounded-2xl w-fit">
        <button 
          onClick={() => { setActiveMode('mastering'); setAiResponse(''); }}
          className={`px-6 py-2.5 rounded-xl font-bold flex items-center gap-2 transition-all ${
            activeMode === 'mastering' ? 'bg-indigo-600 text-white shadow-lg' : 'text-slate-500 hover:text-slate-300'
          }`}
        >
          <Headphones size={18} />
          Mastering
        </button>
        <button 
          onClick={() => { setActiveMode('marketing'); setAiResponse(''); }}
          className={`px-6 py-2.5 rounded-xl font-bold flex items-center gap-2 transition-all ${
            activeMode === 'marketing' ? 'bg-indigo-600 text-white shadow-lg' : 'text-slate-500 hover:text-slate-300'
          }`}
        >
          <Wand2 size={18} />
          Audio Promo
        </button>
        <button 
          onClick={() => { setActiveMode('video'); setAiResponse(''); }}
          className={`px-6 py-2.5 rounded-xl font-bold flex items-center gap-2 transition-all ${
            activeMode === 'video' ? 'bg-indigo-600 text-white shadow-lg' : 'text-slate-500 hover:text-slate-300'
          }`}
        >
          <Camera size={18} />
          Video Marketing
        </button>
        <button 
          onClick={() => { setActiveMode('social'); setAiResponse(''); }}
          className={`px-6 py-2.5 rounded-xl font-bold flex items-center gap-2 transition-all ${
            activeMode === 'social' ? 'bg-indigo-600 text-white shadow-lg' : 'text-slate-500 hover:text-slate-300'
          }`}
        >
          <Share2 size={18} />
          Social Campaign
        </button>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        <div className="bg-slate-900 border border-slate-800 p-6 rounded-2xl h-fit">
          <h3 className="text-xl font-bold mb-4 flex items-center gap-2">
            <Mic2 className="text-indigo-400" size={20} />
            {activeMode === 'mastering' ? 'Describe your mix' : 
             activeMode === 'marketing' ? 'Describe your track' : 
             activeMode === 'video' ? 'Describe your video project' : 'Describe your campaign goal'}
          </h3>
          <textarea
            value={trackDescription}
            onChange={(e) => setTrackDescription(e.target.value)}
            placeholder={
              activeMode === 'mastering' 
              ? "e.g., Heavy electronic track with aggressive bass and clear female vocals..."
              : activeMode === 'marketing'
              ? "e.g., A lo-fi hip hop beat for late-night study sessions..."
              : activeMode === 'video'
              ? "e.g., A cinematic video shot in Tokyo at night, neon lights, fast cuts..."
              : "e.g., Pre-save campaign for my debut album 'Neon Skies'..."
            }
            className="w-full h-48 bg-slate-950 border border-slate-800 rounded-xl p-4 focus:outline-none focus:border-indigo-500 transition-all resize-none mb-4"
          />
          <button
            onClick={handleGenerate}
            disabled={loading || !trackDescription}
            className={`w-full py-4 rounded-xl font-bold flex items-center justify-center gap-2 transition-all shadow-lg ${loading || !trackDescription ? 'bg-slate-800 text-slate-500' : 'bg-indigo-600 hover:bg-indigo-700 text-white shadow-indigo-500/20'}`}
          >
            {loading ? <Loader2 className="animate-spin" /> : <Send size={18} />}
            Generate {activeMode === 'video' ? 'Video Metadata' : 'Campaign'}
          </button>
        </div>

        <div className="bg-slate-900 border border-slate-800 p-6 rounded-2xl min-h-[400px] flex flex-col">
          <h3 className="text-xl font-bold mb-4 flex items-center gap-2">
            <Sparkles className="text-purple-400" size={20} />
            AI Output
          </h3>
          {aiResponse ? (
            <div className="flex-1 overflow-y-auto">
              <div className="bg-slate-950/50 p-6 rounded-xl border border-slate-800 text-slate-300 whitespace-pre-wrap leading-relaxed text-sm md:text-base">
                {aiResponse}
              </div>
              <div className="mt-4 flex gap-2">
                <button 
                  onClick={() => navigator.clipboard.writeText(aiResponse)}
                  className="text-xs bg-slate-800 hover:bg-slate-700 px-3 py-1.5 rounded-lg border border-slate-700 transition-colors"
                >
                  Copy All
                </button>
                <button className="text-xs bg-indigo-600 hover:bg-indigo-700 px-3 py-1.5 rounded-lg border border-indigo-500/20 transition-colors">
                  Save to Catalog
                </button>
              </div>
            </div>
          ) : (
            <div className="flex-1 flex flex-col items-center justify-center text-slate-500 py-10">
              <div className="w-16 h-16 bg-slate-800 rounded-full flex items-center justify-center mb-4 animate-pulse">
                {activeMode === 'mastering' ? <Headphones size={32} className="opacity-30" /> : 
                 activeMode === 'marketing' ? <FileAudio size={32} className="opacity-30" /> : 
                 activeMode === 'video' ? <Video size={32} className="opacity-30" /> : <Share2 size={32} className="opacity-30" />}
              </div>
              <p className="text-center italic max-w-[250px]">Select a tool and provide details on the left to unlock AI-powered music distribution insights.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AIStudio;

