interface VideoUploaderProps {
  onComplete?: () => void;
}

const VideoUploader: React.FC<VideoUploaderProps> = ({ onComplete }) => {
  const [step, setStep] = useState(0); 
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [deliveryLog, setDeliveryLog] = useState<string[]>([]);
  const [ingestionPackage, setIngestionPackage] = useState<string | null>(null);
  const [validationResult, setValidationResult] = useState<string | null>(null);
  const [isVevoConnected] = useState(true); // Assuming for demo

  const [metadata, setMetadata] = useState<VideoMetadata>({
    title: '',
    artist: '',
    director: '',
    producer: '',
    productionCompany: '',
    label: '',
    isrc: '',
    upc: '',
    genre: 'Pop',
    releaseDate: '',
    explicit: false,
    description: ''
  });

  const genres = ['Pop', 'Rock', 'Electronic', 'Hip Hop', 'Latin', 'R&B', 'Country', 'Folk'];

  const handleAudit = async () => {
    setIsSubmitting(true);
    try {
      const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });
      const prompt = `Act as a Vevo and Apple Music Video Compliance Auditor. 
      Check this music video submission for metadata standards:
      - Title: ${metadata.title} (Vevo standard: must not include 'Official Video' or '4K')
      - Artist: ${metadata.artist}
      - Director: ${metadata.director}
      - Production Company: ${metadata.productionCompany}
      - Description: ${metadata.description}

      Provide a strict audit report with 3 concise points regarding compliance with Vevo and Apple Music metadata rules.`;

      const response = await ai.models.generateContent({
        model: 'gemini-3-flash-preview',
        contents: prompt,
      });
      setValidationResult(response.text);
      setStep(2);
    } catch (error) {
      console.error(error);
      setStep(2); 
    } finally {
      setIsSubmitting(false);
    }
  };

  const finalSubmit = async () => {
    setIsSubmitting(true);
    setStep(3);

    const addToLog = (msg: string) => setDeliveryLog(prev => [...prev, `${new Date().toLocaleTimeString()}: ${msg}`]);

    try {
      addToLog("Initiating Vevo Direct Distribution Pipeline...");
      await new Promise(r => setTimeout(r, 800));

      addToLog("Authenticating with Vevo Partner Ingestion API...");
      await new Promise(r => setTimeout(r, 1000));

      addToLog("Generating Vevo XML Metadata Package (DDEX-compliant)...");
      const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });
      const pkgResponse = await ai.models.generateContent({
        model: 'gemini-3-flash-preview',
        contents: `Generate a valid Vevo Ingestion XML (DDEX format snippet) for the following video metadata:
        Title: ${metadata.title}
        Artist: ${metadata.artist}
        ISRC: ${metadata.isrc}
        Director: ${metadata.director}
        Label: ${metadata.label}
        Only return the XML block. No explanation.`,
      });
      setIngestionPackage(pkgResponse.text);
      addToLog("Ingestion package generated successfully.");

      await new Promise(r => setTimeout(r, 1200));
      addToLog("Uploading Master ProRes Asset to US-EAST-1 Node...");
      await new Promise(r => setTimeout(r, 2000));

      addToLog("Syncing metadata with Apple Music Video and Tidal Global...");
      await new Promise(r => setTimeout(r, 1500));

      addToLog("Syndication complete. Video is now in 'Ingesting' status.");
      setIsSubmitting(false);
    } catch (error) {
      addToLog("Error during delivery: " + error);
      setIsSubmitting(false);
    }
  };

  return (
    <div className="max-w-4xl mx-auto animate-in fade-in slide-in-from-bottom-4 duration-500 pb-20">
      <div className="mb-10 text-center">
        <h2 className="text-3xl font-bold mb-2 flex items-center justify-center gap-3">
          <Video className="text-rose-500" size={32} />
          Premium Video Distribution
        </h2>
        <p className="text-slate-400">Direct API syndication to Vevo, Apple Music, and Tidal.</p>
      </div>

      {/* Stepper */}
      {step < 3 && (
        <div className="flex items-center justify-between mb-12 relative max-w-md mx-auto">
          <div className="absolute top-1/2 left-0 w-full h-0.5 bg-slate-800 -z-10 -translate-y-1/2"></div>
          {[0, 1, 2].map((s) => (
            <div 
              key={s}
              className={`w-10 h-10 rounded-full flex items-center justify-center font-bold border-2 transition-all ${
                step >= s ? 'bg-rose-600 border-rose-600 text-white shadow-lg shadow-rose-600/20' : 'bg-slate-900 border-slate-800 text-slate-500'
              }`}
            >
              {step > s ? <CheckCircle size={20} /> : s + 1}
            </div>
          ))}
        </div>
      )}

      <div className="bg-slate-900 border border-slate-800 rounded-3xl p-8 shadow-xl">
        {step === 0 && (
          <div className="space-y-8 animate-in fade-in duration-300">
